import React, { Component } from "react";
import Router from "next/router"
import {Button, Spinner} from "../src/components"
import * as actions from "../store/actions/authActions"

import { connect } from "react-redux"

class Auth extends Component {
  state = {
    registrationFormSelected:false, //boolean to toggle betweeen the login and registration form
    email:"",
    password:"",
    loading: false, //updated by componentDidUpdate
  };
  componentDidMount(){
    //reseting errors on initial page mount
    this.props.clearError()
  }
  componentDidUpdate(prevProps, prevState){
    if(this.props.userToken !== prevProps.userToken){
      this.props.userToken ? Router.push("/") : null
      this.setState({loading: false})
    }
    if(this.props.error !== prevProps.error){
      this.setState({loading: false})
    }
  }
  onSubmitHandler=(event)=>{
    event.preventDefault()
    this.setState({loading: true})
    const {email, password} = this.state
    this.props.login(email,password)
  }

  onChange=(event)=>{
    if(event.target.name === "password" && this.props.error){
      this.props.clearError()
    }
    this.setState({
        [event.target.name]:event.target.value
    })
  }
  render() {
    const button = !this.state.loading ?<Button onClick={this.onSubmitHandler}>Login</Button>: <Spinner />
    let form = (<React.Fragment>
                <form onSubmit={this.onSubmitHandler}>
            <input required value={this.state.email} 
               onChange={this.onChange} 
               style={styles.inputStyle} placeholder="email@gmail.com"
               type="email" name="email" />
            <input required value={this.state.password}
               onChange={this.onChange} 
               style={styles.inputStyle}
               placeholder="password"
               type="password" name="password" />
                        <input required value={this.state.password}
               onChange={this.onChange} 
               style={styles.inputStyle}
               placeholder="confirm password"
               type="password" name="password" />
            {/* displaying button or spinner */}
            {button}
            { this.props.error ? <p style={styles.errorStyle}> {this.props.error} </p>: null }
          </form>
    </React.Fragment>)

    if(this.state.registrationFormSelected)
    return (
      <div style={styles.containerStyle}>
          <div>
            <h1 style={{textAlign:"center", color:"#474A48",fontFamily: 'Lexend Tera, sans-serif', fontWeight:"bold"}}>RoomR<span style={{color:"#FF5941"}}>8</span>r</h1>
            <p style={styles.header}>Gamble with the lottery not your accomodation</p>
          </div>
        <div style={styles.mainAreaStyle}>
          {form}
        </div>
        <p style={styles.paragraphStyle}>
            Dont Have an account? <span className="createAccountButton" onClick={()=>alert("clicked")}>Create Account</span>
          </p>
          <style jsx>{`
          .createAccountButton {
            font-weight: bold;
            font-size:20;
            color:#FF5941;
            text-decoration: underline
          }
          .createAccountButton :hover {
            opacity: 0.7;
            cursor: default
          }
        `}
        </style>
      </div>
    );
  }
}

const styles = {
  containerStyle: {
    height: "100vh",
    width: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor:"#fffffa",
    flexDirection: 'column',
  },
  mainAreaStyle: {
    backgroundColor: "#191923",
    // backgroundColor: "#ccc",    
    // height: "250px",
    width: "300px",
    borderRadius: "5px",
    padding:"20px",
    justifyContent:"center"
  },
  inputStyle: {
    display: "block",
    // padding: "10px",
    height:"36px",
    width: "266px",
    // paddingLeft:"10px",
    margin: "15px auto",
    borderRadius: "5px"
  },
  header:{
    textAlign:"center",
    color:"#191923",
    fontWeight: 'bolder',
    fontSize:22,
    fontFamily: 'Poppins, sans-serif', 
  },
  errorStyle:{
    marginBottom:"20px",
    color:"#f50",
    textAlign:"center",
    fontSize:16,
    fontWeight:"bold"
  },
  paragraphStyle:{
    textAlign:"left",
    padding: 20,
    fontSize:16,
    fontFamily:"Poppins, sans serif",
    backgroundColor:"#fff"
  }
};
const mapStateToProps=(state)=>{
  return {
    userToken: state.auth.userToken,
    error: state.auth.error,
    loading: state.auth.loading
  }
}


export default connect(mapStateToProps,actions)(Auth)
