import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import { Card, CardHeader, CardMedia, CardContent, 
  CardActions,Collapse, Avatar,IconButton, 
  Typography } from "@material-ui/core"

import FavoriteIcon from '@material-ui/icons/Favorite';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Rating from "@material-ui/lab/Rating"

const useStyles = makeStyles(theme => ({
  card: {
    maxWidth: 500,
    marginBottom: 40,
    margin:"0 auto",
    padding:0,
    textAlign:"left",
    boxShadow:"2px 4px 5px 0 rgba(44, 54, 94, 0.4)"
  },
  cardHeader:{
    backgroundColor:"#2C365E",
    color:"#fffffa",
    fontWeight:"bold",
    textTransform:"capitalize",
    fontFamily:'Fira Sans, sans-serif',
  },
  media: {
    margin:10,
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: "#FF5941",
  },
  footer:{
      backgroundColor:"#fffffa"
  },
  icon:{
      color:"#fffffa",
  },
  labelStyles:{
    textTransform:"capitalize", 
    color:"#2C365E",
    fontFamily:"Fira Sans, sans-serif",
    width:"70px"
  },
  resultsText:{
    fontWeight:"bold",
    fontFamily:'Fira Sans, sans-serif',
    color:"#2C365E",
    fontSize:20,
    paddingLeft:10
  }
}));

const ReviewCard=(props)=>{
    const { postal_code, address, landlord_bio, comment } = props.data;
const { first_name, last_name } = landlord_bio;
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick=()=>{
    setExpanded(!expanded);
  }

  return (
    <Card className={classes.card}>
      <CardMedia
        className={classes.media}
        image="/static/imgs/mapView.jpg"
        title="map view image"
      />
      <CardContent>
        <Typography >
          <span className={classes.labelStyles}>Postal Code:</span>
          <span className={classes.resultsText}>
            {postal_code}
          </span>
        </Typography>
        {/* street address */}
        <Typography >
          <span className={classes.labelStyles}>Street Address:</span>
          <span className={classes.resultsText}>
            {address.street_address}
          </span>
        </Typography>
        <Typography>
          <span className={classes.labelStyles}>City:</span>
          <span className={classes.resultsText}>
            {""}
          </span>
        </Typography>
      </CardContent>
      <CardActions className={classes.footer}>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon className={classes.icon}/>
        </IconButton>
        <Rating value={props.data.rating? props.data.rating : 0} readOnly style={{color:"#FF5941", marginLeft: 20}}/>
        <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded,
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMoreIcon style={{color:"#FF5941"}}/>
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography>
          {comment}
          </Typography>
        </CardContent>
      </Collapse>
    </Card>
  );
}

export { ReviewCard }
