import React, { Component } from "react";
import {connect} from "react-redux"

class LandlordForm extends Component {
  state = {
    landlord_bio: "",
    name: ""
  };

  landLordformSubmitHandle = event => {
    event.preventDefault(); //preventing page refresh
  };

  onChangeText = (event) => {
    /* handles input change for the form*/
    const {name,value} = event.target
    this.setState({
      [name]: value
    });
    console.log(this.state)
  };
  render() {
    return (
        <form onSubmit={this.landLordformSubmitHandle}>
          <input
            placeholder="John Doe"
            type="text" style={styles.inputStyle}
            name="name"
            onChange={this.onChangeText}
          />
          <textarea
            placeholder="bio" onChange={this.onChangeText}
            name="landlord_bio"
            cols="10" rows="6"
            style={styles.inputStyle}
          />
        </form>
    );
  }
}
const styles = {
  inputStyle: {
    padding: 15,
    width: "300px",
    borderRadius: 7,
    fontSize: 14,
    marginTop: "10px",
    marginBottom: "10px",
    display: "block"
  }
};

