import React, { Component,useState } from "react";

import ModalComponent from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";
import Rating from "@material-ui/lab/Rating";
import axios from "axios"

import  HorizontalStepper from "../stepper"
import { Button } from "../components";

  class addReviewForm extends Component{
  constructor(props){
    super(props)
      this.handleScriptLoad = this.handleScriptLoad.bind(this)
    this.handlePlaceSelect = this.handlePlaceSelect.bind(this)
  }

  state={
    rating:0,
    postal_code:"",
    street_number:"",
    apartment_number:"",
    first_name:"",
    last_name:"",
    comment:"",
    city:""
  }
  /*** start of google maps functions**/ 

  handleScriptLoad=()=>{
    /***loads google script on load */
    // Declare Options For Autocomplete
    const options = {
      // types: ['(cities)'],
    };

    // Initialize Google Autocomplete
    this.autocomplete = new google.maps.places.Autocomplete(
      document.getElementById('autocomplete'),
      options,
    );

    // Avoid paying for data that you don't need by restricting the set of
    // place fields that are returned to just the address components.
    this.autocomplete.setFields(['address_components']);

    // Fire Event when a suggested name is selected
    this.autocomplete.addListener('place_changed', this.handlePlaceSelect);
  }

  handlePlaceSelect=()=> {
    // Extract City From Address Object
    let addressObject = this.autocomplete.getPlace();
    let address = addressObject.address_components;

    // Check if address is valid
    if (address) {
      console.log(address)
      this.setState(
        {
          city: address[0].long_name,
        }
      );
    }
  }



  handleClose = () => {
    this.props.hideModalHandle();
  };

  onInputChange=(event)=>{
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  addReviewHandle=(event)=>{
    event.preventDefault()
    axios.post(`https://accomo-rater.firebaseio.com/landlord_data.json?auth=${
      this.props.userToken}`,{
        "postal_code":this.state.postal_code,
        "address":{
          "street_number":this.state.street_number,
          "apartment_number":this.state.apartment_number
          },
        "landlord_bio":{
          "first_name":this.state.first_name,
          "last_name":this.state.last_name
        },
        "comment":this.state.comment,
        "rating": this.state.rating
        }
    ).then(response=>{
        this.handleClose()
    })
  }
  render(){
    const para = <p style={{color:"#fff"}}>This is a paragraph</p>
  return (
    <div>
      {/* loading google maps script */}
      <Script
        url="https://maps.googleapis.com/maps/api/js?key=AIzaSyBWcDVGfs8R5EmWbh9xsuYEHycoqDyDf9I&libraries=places"
          onLoad={this.handleScriptLoad}
        />  
      <ModalComponent aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        style={styles.modal}
        open={this.props.modalVisible}
        onClose={this.handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500
        }}
      >
        <Fade in={this.props.modalVisible}>
          <div style={styles.paper}>
            <h2 style={styles.heading}
              id="transition-modal-title" >
              Add Review
            </h2>
            {/* <form onSubmit={this.addReviewHandle}>
              <input onChange={this.onInputChange} style={styles.input} 
                type="text" value={this.state.postal_code}
                required placeholder="Post Code" name="postal_code"/>
              <input onChange={this.onInputChange}  style={styles.input} 
                type="text" name="street_number"
                required placeholder="Street address" value={this.state.street_number} />
              <input onChange={this.onInputChange}  style={styles.input} 
                type="text" placeholder="Apartment Number"
                name="apartment_number" value={this.state.apartment_number}
              />
              <input onChange={this.onInputChange} style={styles.input}
                type="text" required
                placeholder="City"
                name="city" value={this.state.city}
              />
                <input onChange={this.onInputChange} style={styles.input} 
                  placeholder="landlord's name" name="first_name" 
                  value={this.state.first_name}/>
                <textarea onChange={this.onInputChange} style={styles.input} placeholder="Feedback" name="comment" value={this.state.comment}/>
              <span style={{
                margin: "15px auto",
                justifyContent: "center",
                display: "flex"
              }}>
                <Rating
                  value={this.state.rating}
                  onChange={(event, newValue) => this.setState({rating: newValue})}
                />
              </span>
              <Button onClick={this.addReviewHandle} style={styles.buttonStyle}>
                Submit
              </Button>
            </form> */}
            <HorizontalStepper closeModal={this.handleClose} para={para}/>
          </div>
        </Fade>
      </ModalComponent>
    </div>
  )}
};

const styles = {
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "rgba(44,54,94,0.8)"
  },
  paper: {
    margin: "0 auto",
    alignContent: 'center',
    borderRadius: "10px",
    background: "rgb(44,54,94)",
    width: 600,
    padding: "4px, 4px, 3px"  
  },
  input: {
    padding: 15,
    borderRadius: "5px",
    display: "block",
    margin: "15px auto",
    width:200
  },
  heading:{
    textAlign: "center",
    fontFamily: "Poppins, sans-serif",
    color: "#fffff0"
  }
};

export default addReviewForm;
