import React, { Component,useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import ModalComponent from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";
import Rating from "@material-ui/lab/Rating";

import axios from "axios"

import { Button } from "../components";

const styles = {
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "rgba(44,54,94,0.8)"
  },
  paper: {
    margin: "0 auto",
    borderRadius: "3px",
    background: "rgb(44,54,94)",
    boxShadow: "#000",
    width: 320,
    padding: "4px, 4px, 3px"
  },
  input: {
    padding: 15,
    display: "block",
    margin: "10px auto"
  },
  heading:{
    textAlign: "center",
    fontFamily: "Poppins, sans-serif",
    color: "#fffff0"
  }
};

  class addReviewForm extends Component{
  constructor(props){
    super(props)
    this.state={
      rating:1
    }


  }

  handleClose = () => {
    this.props.hideModalHandle();
  };

  addReviewHandle=(event)=>{
    event.preventDefault()
    axios.post(`https://accomo-rater.firebaseio.com/landlord_data.json?auth=${
      this.props.userToken}`,{
        "postal_code":"T5H-1M9",
        "address":{
          "street_number":"10633 129ave",
          "apartment_number":"401"
          },
        "landlord_bio":{
          "first_name":"Mo",
          "last_name":""
        },
        "comment":"Truly a great guy. landlords could learn a lot from this guy",
        "time_stamp":"1565893599"
        }
    ).then(response=>{
        // handleClose()
    })
  }
  render(){
  return (
    <div>
      <ModalComponent aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        style={styles.modal}
        open={this.props.modalVisible}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500
        }}
      >
        <Fade in={this.props.modalVisible}>
          <div style={styles.paper}>
            <h2 style={styles.heading}
              id="transition-modal-title" >
              Add Review
            </h2>
            <form onSubmit={addReviewHandle}>
              <input style={styles.input} type="text"
                required placeholder="Post Code" />
              <input  style={styles.input} type="text" 
                required placeholder="Street address"  />
              <input  style={styles.input} 
                type="text" placeholder="Apartment Number"
              />
              <input style={styles.input}
                type="text" required
                placeholder="City"
              />
              <legend title="Land lord info">
                <input style={styles.input} placeholder="Full Name" />
              </legend>
              <span style={{
                margin: "15px auto",
                justifyContent: "center",
                display: "flex"
              }}>
                <Rating
                  value={this.state.rating}
                  onChange={(event, newValue) => this.setState({rating: event})}
                />
              </span>
              <Button onClick={addReviewHandle} style={styles.buttonStyle}>
                Submit
              </Button>
            </form>
          </div>
        </Fade>
      </ModalComponent>
    </div>
  )}
};

export default addReviewForm;
