import React, {Component} from "react"
import Rating from "@material-ui/lab/Rating";

import { Button } from "../components";
import {makeStyles} from "@material-ui/core/styles"

const styles = makeStyles(theme => ({
    paper: {
        display:"block",
        margin: "0 auto",
        borderRadius: "3px",
        background: "rgb(44,54,94)",
        boxShadow: "#000",
        width: 320,
        padding: theme.spacing(4, 4, 3)
      },
      input: {
        padding: 15,
        display: "block",
        margin: "10px auto"
      },
      heading:{
        textAlign: "center",
        fontFamily: "Poppins, sans-serif",
        color: "#fffff0"
      }
}))

class addReviewForm extends Component {
    state={
      rating:0
    }

    addReviewHandle=()=>{
        axios.post(`https://accomo-rater.firebaseio.com/landlord_data.json?auth=${
          userToken}`,{}
        )
    }
    render(){
      return(
        <div className={styles.paper}>
            <h2 className={styles.heading}
              id="transition-modal-title" >
              Add Review
            </h2>
            <form>
              <input
                className={styles.input}
                type="text"
                required
                placeholder="Post Code"
              />
              <input
                required
                className={styles.input}
                type="text"
                placeholder="Street address"
              />
              <input
                className={styles.input}
                type="text"
                placeholder="Apartment Number"
              />
              <input
                required
                className={styles.input}
                type="text"
                placeholder="City"
              />
              <legend title="Land lord info">
                <input className={styles.input} placeholder="Full Name" />
              </legend>
              <span style={{
                margin: "15px auto",
                justifyContent: "center",
                display: "flex"
              }}>
                <Rating
                  value={this.state.rating}
                  onChange={(event, newValue) => setRating(newValue)}
                />
              </span>
              <Button onClick={this.addReviewHandle}>
                Submit
              </Button>
            </form>
          </div>)
    }
}

export default addReviewForm
