import React, { Component } from "react";
import {connect} from "react-redux"

import {setFormFields} from "../../../store/actions/appActions"

class LandlordForm extends Component {
  state = {
    landlord_review: "",
    name: ""
  };

  landLordformSubmitHandle = event => {
    event.preventDefault(); //preventing page refresh
    this.props.updateFields(this.state)
  };

  onChangeText = (event) => {
    /* handles input change for the form*/
    const {name,value} = event.target
    this.setState({
      [name]: value
    });
  }

  componentDidMount(){
    const {landlord_review,name} = this.props.landlord_bio
    /** auto populating fields if user had started input*/ 
    this.setState({ landlord_review,name })
  }
  render() {
    return (
        <form onSubmit={this.landLordformSubmitHandle}>
          <input
            placeholder="John Doe"
            type="text" style={styles.inputStyle}
            name="name"
            onChange={this.onChangeText}
          />
          <textarea
            placeholder="bio" onChange={this.onChangeText}
            name="landlord_review"
            cols="10" rows="6"
            style={styles.inputStyle}
          />
        </form>
    );
  }
}
const styles = {
  inputStyle: {
    padding: 15,
    width: "300px",
    borderRadius: 7,
    fontSize: 14,
    marginTop: "10px",
    marginBottom: "10px",
    display: "block"
  }
};

const mapStateToProps=(state)=>{
  return{
    landlord_bio:state.app.stepperFormData.landlord_bio
  }
}

const mapDispatchTopProps=(dispatch)=>{
  return{
    updateFields:(data)=>dispatch(setFormFields(data))
  }
}
export default connect(mapStateToProps,mapDispatchTopProps)(LandlordForm)
